const GOOGLE_SCRIPT_URL = 'https://script.google.com/macros/s/AKfycbwXwHQxHyWdEPHYMnVWWPxTQvbAOvNWMWXWnKJbFDDwOYwZEPKlNXSBKOFgBLXmxPDz/exec';

                async function submitToGoogleSheets(formData) {
                    try {
                        const response = await fetch(GOOGLE_SCRIPT_URL, {
                            method: 'POST',
                            mode: 'no-cors',
                            headers: {
                                'Content-Type': 'application/json',
                            },
                            body: JSON.stringify({
                                timestamp: new Date().toLocaleString('fr-FR'),
                                name: formData.get('name'),
                                company: formData.get('company'),
                                email: formData.get('email'),
                                phone: formData.get('phone'),
                                subject: formData.get('subject'),
                                message: formData.get('message')
                            })
                        });

                        return true;
                    } catch (error) {
                        console.error('Erreur lors de l\'envoi:', error);
                        return false;
                    }
                }

                function validateForm(event) {
                    event.preventDefault();
                    let isValid = true;
                    const form = document.getElementById('contactForm');
                    const formGroups = form.getElementsByClassName('form-group');

                    // Réinitialiser tous les états
                    Array.from(formGroups).forEach(group => {
                        group.classList.remove('error', 'success');
                    });

                    // Validation du nom
                    const name = form.name.value;
                    if (name.length < 2) {
                        showError(form.name, 'Le nom doit contenir au moins 2 caractères');
                        isValid = false;
                    } else {
                        showSuccess(form.name);
                    }

                    // Validation de l'email
                    const email = form.email.value;
                    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                    if (!emailRegex.test(email)) {
                        showError(form.email, 'Veuillez entrer une adresse email valide');
                        isValid = false;
                    } else {
                        showSuccess(form.email);
                    }

                    // Validation du téléphone si rempli
                    const phone = form.phone.value;
                    if (phone && !/^[0-9\s]{10}$/.test(phone.replace(/\s/g, ''))) {
                        showError(form.phone, 'Veuillez entrer un numéro de téléphone valide (10 chiffres)');
                        isValid = false;
                    } else if (phone) {
                        showSuccess(form.phone);
                    }

                    // Validation du sujet
                    if (!form.subject.value) {
                        showError(form.subject, 'Veuillez sélectionner un sujet');
                        isValid = false;
                    } else {
                        showSuccess(form.subject);
                    }

                    // Validation du message
                    if (form.message.value.length < 10) {
                        showError(form.message, 'Le message doit contenir au moins 10 caractères');
                        isValid = false;
                    } else {
                        showSuccess(form.message);
                    }

                    // Si tout est valide, envoyer à Google Sheets
                    if (isValid) {
                        const submitButton = form.querySelector('.submit-button');
                        const originalText = submitButton.innerHTML;
                        submitButton.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Envoi en cours...';
                        submitButton.disabled = true;

                        const formData = new FormData(form);

                        submitToGoogleSheets(formData)
                            .then(success => {
                                if (success) {
                                    showNotification('Message envoyé avec succès !', 'success');
                                    form.reset();
                                    Array.from(formGroups).forEach(group => {
                                        group.classList.remove('success');
                                    });
                                } else {
                                    showNotification('Erreur lors de l\'envoi. Veuillez réessayer.', 'error');
                                }
                            })
                            .catch(error => {
                                console.error('Erreur:', error);
                                showNotification('Erreur lors de l\'envoi. Veuillez réessayer.', 'error');
                            })
                            .finally(() => {
                                submitButton.innerHTML = originalText;
                                submitButton.disabled = false;
                            });
                    }

                    return false;
                }

                function showNotification(message, type) {
                    const notification = document.createElement('div');
                    notification.className = `notification ${type}`;
                    notification.innerHTML = `
                        <i class="fas fa-${type === 'success' ? 'check-circle' : 'exclamation-circle'}"></i>
                        ${message}
                    `;
                    document.body.appendChild(notification);

                    setTimeout(() => {
                        notification.classList.add('show');
                    }, 100);

                    setTimeout(() => {
                        notification.classList.remove('show');
                        setTimeout(() => notification.remove(), 300);
                    }, 3000);
                }

                function showError(input, message) {
                    const formGroup = input.closest('.form-group');
                    formGroup.classList.add('error');
                    formGroup.classList.remove('success');
                    const errorDisplay = formGroup.querySelector('.error-message');
                    errorDisplay.style.display = 'block';
                    errorDisplay.textContent = message;
                }

                function showSuccess(input) {
                    const formGroup = input.closest('.form-group');
                    formGroup.classList.add('success');
                    formGroup.classList.remove('error');
                    const errorDisplay = formGroup.querySelector('.error-message');
                    errorDisplay.style.display = 'none';
                }

                // Formatage automatique du numéro de téléphone
                document.getElementById('phone').addEventListener('input', function(e) {
                    let x = e.target.value.replace(/\D/g, '').match(/(\d{0,2})(\d{0,2})(\d{0,2})(\d{0,2})(\d{0,2})/);
                    e.target.value = !x[2] ? x[1] : x[1] + ' ' + x[2] + (!x[3] ? '' : ' ' + x[3] + (!x[4] ? '' : ' ' + x[4] + (!x[5] ? '' : ' ' + x[5])));
                });
